mapper.select:
select 
FROM t_payment_info
WHERE sys_del=0

  AND course_id=#{courseId}


  AND station_id=#{stationId}


  AND status=#{status}

order by update_time desc
LIMIT #{page.startIndex,jdbcType=INTEGER},#{page.pageSize,jdbcType=INTEGER}

select COUNT (*)
FROM t_payment_info
WHERE sys_del=0

  AND course_id=#{courseId}


  AND station_id=#{stationId}


  AND status=#{status}

select 
FROM t_payment_info
WHERE sys_del=0
AND student_id=#{studentId}
order by update_time desc
LIMIT #{page.startIndex,jdbcType=INTEGER},#{page.pageSize,jdbcType=INTEGER}
